sp_widget.client_script:
function($rootScope, $scope) {
  /* widget controller */
  var c = this;
	c.selectItem = function(idx){
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);

	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */


	data.notes = [];
	var currentUser = gs.getUser().getID();
	var userRecords = new GlideRecord('x_snc_createnotes_note');
	userRecords.addQuery('user.sys_id', currentUser);
	userRecords.orderBy('sys_created_on');
	userRecords.query();

	while(userRecords.next()){
		var noteObj = {};
		noteObj.number = userRecords.getValue('number');
		noteObj.title = userRecords.getValue('title');
		noteObj.sys_id = userRecords.getValue('sys_id');

		//gs.info(noteObj);
		var desc = userRecords.getValue('note');

		noteObj.short_description = desc.slice(0,21);

		data.notes.push(noteObj);


	}
//	console.log(data.notes);





})();
